/* CoolMenus 4 - default styles - do not edit */
.clCMEvent{position:absolute; width:99%; height:99%; clip:rect(0,100%,100%,0); left:0; top:0; visibility:visible}
.clCMAbs{position:absolute; visibility:hidden; left:0; top:0}
/* CoolMenus 4 - default styles - end */

/*Style for the background-bar*/
.clBar{position:absolute; width:10; height:10; background-color:#FFFFFF; layer-background-color:#FF9900; visibility:hidden}

/*Styles for level 0*/
.clLevel0,.clLevel0over{
        position:absolute;
        padding:1px;
        font-family:inherit;
        font-size:11px;
        font-weight:inherit;
        left: 40px;
        padding-left: 21px;
}
.clLevel0{
        color:#000000;
        background-image: url(images/menu_left_26.gif);
}
.clLevel0over{
		background-color:#febd15; 
		layer-background-color:#989C8b; 
		color:#FFFFFF; 
		cursor:pointer; 
		cursor:hand; 
}
