/*Styles for level 1*/
.clLevel1, .clLevel1over{position:absolute; padding:2px; font-family:inherit; font-size:11px; font-weight:inherit;}
.clLevel1{background-color:#f5f5f5; layer-background-color:#989C8b; color:#000000;}
.clLevel1over{background-color:#febd15; layer-background-color:#989C8b; color:#FFFFFF; cursor:pointer; cursor:hand; }
.clLevel1border{
        position:absolute;
        visibility:hidden;
        background-color:#D9D9D9;
        layer-background-color:#D9D9D9;
}

/*Styles for level 2*/
.clLevel2, .clLevel2over{position:absolute;  padding:2px; font-family:inherit; font-size:10px; font-weight:inherit}
.clLevel2{background-color:Navy; layer-background-color:Navy; color:white;}
.clLevel2over{background-color:#0099cc; layer-background-color:#0099cc; color:Yellow; cursor:pointer; cursor:hand; }
.clLevel2border{position:absolute; visibility:hidden; background-color:#006699; layer-background-color:#006699}
